package org.tnsindia.datatypedemo;

public class ByteDataTypeDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int xyz=120;
	      long abc=2147483648L;
	    	  System.out.println("the value is:"+xyz);
	    	  System.out.println("the value is:"+abc);
	    	  char pqr='z';
	    	  float c=12.89f;
	    	  double d=41.6;
	    	  String str="kalpana";
	    	  @SuppressWarnings("unused")
	  		boolean var=true;
	      	  int x=(int)12.5;
	      	  float y=(float)34;
	      	  System.out.println("the value is:"+pqr);
	      	  System.out.println("the value is:"+c);
	      	  System.out.println("the value is:"+d);
	      	  System.out.println("the value is:"+x);
	      	  System.out.println("the value is:"+y);
	      	  System.out.println("the value is:"+str);
	      	  
	        
	  	}
}

	
